import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { map, Observable, of, tap } from 'rxjs';
import { EscuelaService } from '../services/escuela.service';

@Component({
  selector: 'app-register-calificacion',
  templateUrl: './register-calificacion.component.html',
  styleUrls: ['./register-calificacion.component.css']
})
export class RegisterCalificacionComponent implements OnInit {

  getAllMaterias$: Observable<any> = of();

  lista:string[]=["hola","que","tal", "estas"];
  seleccionado:any;;

  constructor(
    private route: ActivatedRoute,
    private escuelaService: EscuelaService
  ) { }

  ngOnInit(): void {
    const routeParams = this.route.snapshot.paramMap;
    const alumnoIdFromRoute = Number(routeParams.get('alumnoId'));

    this.getAllMaterias$ = this.escuelaService
      .getAllMaterias()
      .pipe(
        map(d => d.data),
        tap(console.log),
      )

      this.escuelaService
      .getAllMaterias()
      .pipe(
        map(d => d.data),
        tap(console.log),
      ).subscribe()
  }

}
<p>register-calificacion works!</p>

<div>
    <select name="miselect" [(ngModel)]="seleccionado">
        <option [value]="item" *ngFor="let item of lista">{{item}}</option>
    </select>
</div>
