import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { map, Observable, of, tap } from 'rxjs';
import { EscuelaService } from '../services/escuela.service';

@Component({
  selector: 'app-register-calif',
  templateUrl: './register-calif.component.html',
  styleUrls: ['./register-calif.component.css']
})
export class RegisterCalifComponent implements OnInit {

  getAllMaterias$: Observable<any> = of();

  languageObjects:any[]=[
    {id: 1, name: "Angular"},
    {id: 2, name: "Typescript"},
    {id: 3, name: "Javascript"},
    {id: 4, name: "Java"},
    {id: 5, name: "DotNet"}
  ]
  selectedObject:any;

  constructor(
    private route: ActivatedRoute,
    private escuelaService: EscuelaService
  ) { }

  ngOnInit(): void {
    const routeParams = this.route.snapshot.paramMap;
    const alumnoIdFromRoute = Number(routeParams.get('alumnoId'));

    this.getAllMaterias$ = this.escuelaService
      .getAllMaterias()
      .pipe(
        map(d => d.data),
        tap(console.log),
      )

      this.escuelaService
      .getAllMaterias()
      .pipe(
        map(d => d.data),
        tap(console.log),
      ).subscribe()
  }

}
<p>Registrar Calificacion</p>

<div>
    <label >Materia</label>
    <select [(ngModel)]="selectedObject">
        <option *ngFor="let materia of getAllMaterias$ | async" [ngValue]="materia">
             {{materia.nombre}}
         </option>
    </select>
</div>

<div>
    <label >Calificacion</label>
    <input type="number" min="1" max="10">
</div>

